"""
Find the largest product in a series
"""

DA_NUMBA = ""

def get_numbers(num):
    test_numbers = []
    for x in [x for x in DA_NUMBA.split('0') if len(x) >= num]:
        if len(x) > num:
            for i in range(len(x) - num + 1):
                test_numbers.append(x[i:num + i])
        else:
            test_numbers.append(x)

    return test_numbers

def largest_product(num):
    product = 0

    test_numbers = get_numbers(num)
    for number in test_numbers:
        res = 1
        for x in number:
            res *= int(x)

        product = product if product > res else res

    return product

def main():
    product = largest_product(13)
    print(product)

if __name__ == "__main__":
    main()
